const a=[{id:1,type:"news",title:"Шумоизоляция вашего авто — теперь за 1 день!",image:"img/news_img_1.png",category:"news",date:"2024-01-15",views:245,content:"Ищете где быстро сделать шумоизоляцию вашего автомобиля? Теперь мы предлагаем услугу шумоизоляции за 1 день без потери качества услуги! Наши мастера используют только профессиональные материалы и современное оборудование для достижения идеального результата.",tags:["Новости"],priority:1},{id:2,type:"news",title:"Антихром — современное решение для автомобиля",image:"img/news_img_2.png",category:"news",date:"2024-01-12",views:189,content:"Антихром становится всё популярнее среди владельцев премиальных автомобилей. Это решение позволяет изменить внешний вид хромированных элементов, сделав автомобиль более стильным и современным. Узнайте о преимуществах этого решения.",tags:["Новости"],priority:2},{id:3,type:"news",title:"Расширение услуг: теперь доступны все марки BMW",image:"img/news_img_1.png",category:"news",date:"2024-01-10",views:156,content:"Мы рады сообщить, что теперь выполняем работы по тюнингу для всех моделей BMW, включая последние новинки. Наши специалисты прошли специальное обучение и имеют опыт работы с автомобилями этой марки.",tags:["Новости"],priority:3},{id:4,type:"news",title:"Новые материалы для карбонового тюнинга",image:"img/news_img_2.png",category:"news",date:"2024-01-08",views:203,content:"В наш ассортимент добавлены премиальные карбоновые материалы от ведущих мировых производителей. Теперь мы можем предложить еще больше вариантов для создания уникального внешнего вида вашего автомобиля.",tags:["Новости"],priority:4},{id:5,type:"article",title:"Зачем нужна шумоизоляция автомобиля",category:"articles",date:"2024-01-14",views:312,content:"Шумоизоляция автомобиля — это не роскошь, а необходимость для комфортного вождения. В нашей подробной статье мы расскажем о преимуществах профессиональной шумоизоляции, материалах и процессе установки. Узнайте, почему стоит инвестировать в акустический комфорт вашего автомобиля.",tags:["Статья"],priority:5},{id:6,type:"article",title:"Руководство по выбору антихрома для вашего авто",category:"articles",date:"2024-01-11",views:278,content:"Антихром — это современное решение для изменения внешнего вида автомобиля. В этой статье мы подробно расскажем о том, как выбрать подходящий вариант антихрома для вашей модели, какие материалы используются и как происходит процесс нанесения.",tags:["Статья"],priority:6},{id:7,type:"article",title:"Преимущества карбонового тюнинга",category:"articles",date:"2024-01-09",views:195,content:"Карбоновый тюнинг не только улучшает внешний вид автомобиля, но и влияет на его технические характеристики. Узнайте о всех преимуществах карбоновых элементов, их влиянии на аэродинамику и почему этот материал выбирают владельцы премиальных автомобилей.",tags:["Статья"],priority:7},{id:8,type:"article",title:"Как ухаживать за автомобилем после тюнинга",category:"articles",date:"2024-01-07",views:167,content:"Правильный уход за тюнингованными элементами — залог их долговечности. Мы расскажем о том, как сохранить первозданный вид вашего автомобиля после тюнинга, какие средства использовать для ухода и как часто нужно проводить обслуживание.",tags:["Статья"],priority:8},{id:9,type:"article",title:"Скидка до 50% на антихром с покраской",category:"actions",date:"2024-01-13",views:445,content:'Ограниченное предложение на услуги "Антихром с помощью покраски". Успей воспользоваться предложением по выгодной цене! Акция действует до конца месяца. Качество работ гарантируем, все материалы профессионального уровня.',tags:["Акция"],priority:9},{id:10,type:"article",title:"Комплексный тюнинг со скидкой 30%",category:"actions",date:"2024-01-06",views:389,content:"Специальное предложение для комплексного тюнинга: антихром + шумоизоляция + карбоновые элементы со скидкой 30%. Экономьте на полном преображении вашего автомобиля. Идеальное решение для тех, кто хочет получить максимум результата.",tags:["Акция"],priority:10},{id:11,type:"article",title:"Бесплатная диагностика для новых клиентов",category:"actions",date:"2024-01-05",views:234,content:"Новые клиенты могут получить бесплатную диагностику состояния автомобиля и консультацию по возможным вариантам тюнинга. Предложение ограничено! Наши специалисты оценят текущее состояние вашего автомобиля и предложат оптимальные решения.",tags:["Акция"],priority:11},{id:12,type:"article",title:"Сезонная скидка на шумоизоляцию",category:"actions",date:"2024-01-03",views:198,content:"Подготовьте ваш автомобиль к весеннему сезону! Скидка 25% на все виды шумоизоляции до конца марта. Качество работ гарантируем. Профессиональная установка, использование лучших материалов и длительная гарантия на все работы.",tags:["Акция"],priority:12}];function r(){return a.sort((e,t)=>new Date(t.date)-new Date(e.date)).slice(0,4)}function o(e){return a.find(t=>t.id===parseInt(e))}function s(e){const t=new Date(e),n=Math.abs(new Date-t),i=Math.ceil(n/(1e3*60*60*24));return i===1?"Сегодня":i===2?"Вчера":i<=7?`${i} дней назад`:t.toLocaleDateString("ru-RU",{day:"numeric",month:"long",year:"numeric"})}window.NEWS_DATA=a;window.getLatestNewsForMain=r;window.getNewsById=o;window.formatDate=s;
